mapper.select:
SELECT u.user_id, u.dept_id, u.login_name, u.user_name, u.user_type, u.openid, u.email, u.mobile, u.password, u.gender, u.post, u.avatar, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.customer_id, u.park_id, u.create_by, u.create_time, u.remark, d.dept_name, NULL AS role_id FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.dept_id WHERE u.del_flag = '0'
AND u.login_name like concat('%', #{loginName}, '%')
AND u.user_type = #{userType.ordinal}
AND u.status = #{status}
AND u.mobile like concat('%', #{mobile}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
${params.dataScope}

SELECT DISTINCT u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.mobile, u.status, u.create_time FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.dept_id LEFT JOIN sys_user_role ur ON u.user_id = ur.user_id LEFT JOIN sys_role r ON r.role_id = ur.role_id WHERE u.del_flag = '0' AND r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.mobile like concat('%', #{mobile}, '%')
${params.dataScope}

mapper.update:
UPDATE sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, user_type = #{userType, typeHandler=com.ruoyi.common.handlers.MybatisEnumTypeHandler}, openid = #{openid}, email = #{email}, mobile = #{mobile}, gender = #{gender}, post = #{post}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, customer_id = #{customerId}, park_id = #{parkId}, remark = #{remark}, update_time = sysdate()
WHERE user_id = #{userId}
